// 4 Converteer schema tijden naar minuten sinds middernacht
// Functie timeToMinutes converteert bijv. "17:00" naar 1020 minuten (17*60 + 0)
// Dit maakt het vergelijken van tijden simpel en betrouwbaar.
function timeToMinutes(timeStr) {
    let parts = timeStr.split(':');          // splits "HH:MM" in een array [HH, MM]
    return parseInt(parts[0], 10) * 60 + parseInt(parts[1], 10); // uren naar minuten + minuten
}

// Pas de functie toe op start- en eindtijd van het schema
let startMinutes = timeToMinutes(schedule.startTime);
let endMinutes = timeToMinutes(schedule.endTime);

// 5 Beslis of we moeten uitschakelen of inschakelen
