// Implement load shifting between 17:00 and 20:00
var assets = flow.get('assets') || [];
var now = new Date();
var hour = now.getHours();
var payload = [];

for(var i=0; i<assets.length; i++) {
    var asset = assets[i];
    var load = 0;
    // Only allow consumption in off-peak hours, i.e., shift load to 17-20
    if(hour >= 17 && hour < 20 && asset.available) {
        load = asset.maxLoad;
    }
    payload.push({ asset: asset.name, scheduledLoad: load, status: asset.available ? 'active' : 'inactive' });
}

msg.payload = payload;
return msg;

// Simulate actual consumption with 5% variation
var commands = msg.payload;
var measured = commands.map(function(cmd) {
    var actualLoad = Math.max(0, cmd.scheduledLoad * (0.95 + 0.1 * Math.random()));
    return { asset: cmd.asset, scheduledLoad: cmd.scheduledLoad, actualLoad: actualLoad, status: cmd.status };
});

msg.payload = measured;
return msg;

// Calculate total shifted load
var measured = msg.payload;
var totalScheduled = 0;
var totalActual = 0;
for(var i=0; i<measured.length; i++) {
    totalScheduled += measured[i].scheduledLoad;
    totalActual += measured[i].actualLoad;
}

msg.payload = {
    totalScheduled: totalScheduled,
    totalActual: totalActual,
    compliance: totalActual >= totalScheduled * 0.95,
    details: measured
};
return msg;

// ================================
// Berekening van totale verschoven belasting
// ================================

// Verkrijg de gemeten waarden uit de inkomende boodschap
// msg.payload wordt verondersteld een array van objecten te zijn, 
// waarbij elk object twee eigenschappen heeft:
// - scheduledLoad: de geplande belasting voor deze periode
// - actualLoad: de daadwerkelijk gemeten belasting voor deze periode
var measured = msg.payload;

// Initialiseer variabelen om de som van de geplande en werkelijke belastingen bij te houden
var totalScheduled = 0; // accumulator voor alle geplande belastingen
var totalActual = 0;    // accumulator voor alle werkelijke belastingen

// Controle op randgeval: als de array leeg is
// (optioneel, niet in de originele code, maar nuttig in productie)
if (!Array.isArray(measured) || measured.length === 0) {
    // Als er geen data is, zijn de totalen nul en compliance false
    msg.payload = {
        totalScheduled: 0,
        totalActual: 0,
        compliance: false,
        details: []
    };
    return msg;
}

// Itereer door elk element in de array
for (var i = 0; i < measured.length; i++) {
    var item = measured[i]; // huidige meetwaarde

    // Voeg de geplande belasting toe aan het totaal
    // scheduledLoad wordt verondersteld een numerieke waarde te zijn
